const numShapes=3,maxSize=200;let colors=[];function setup(){colors=[color(255,143,0,80),color(255,128,171,80),color(255,193,7,80),color(76,175,80,80),color(0,188,212,80),color(171,71,188,80),color(239,83,80,80)],createCanvas(window.innerWidth,document.body.offsetHeight),noStroke()}function randomNumber(o){return Math.floor(Math.random()*o)}function randomChoice(o){return o[randomNumber(o.length)]}function mouseClicked(){let o=randomNumber(maxSize);fill(randomChoice(colors));let e=randomNumber(numShapes);e%numShapes==0?ellipse(mouseX,mouseY,o,o):e%numShapes==1?rect(mouseX,mouseY,o,o):triangle(mouseX,mouseY,mouseX+o,mouseY,mouseX+.5*o,mouseY-o)}window.onresize=(()=>{resizeCanvas(window.innerWidth,document.body.offsetHeight)});